static int pure64_mkgpt(struct pure64_util *util, int argc, const char **argv) {

	struct pure64_uuid disk_uuid;

	if (pure64_uuid_parse(&disk_uuid, PURE64_DEFAULT_DISK_UUID) != 0) {
		fprintf(stderr, "Failed to parse default disk UUID.\n");
		return EXIT_FAILURE;
	}

	for (int i = 0; i < argc; i++) {
		if (strcmp(argv[i], "--disk-uuid") == 0) {
			if ((i + 1) >= argc) {
				fprintf(stderr, "Disk UUID not given.\n");
				return EXIT_FAILURE;
			}
			if (pure64_uuid_parse(&disk_uuid, argv[i + 1]) != 0) {
				fprintf(stderr, "Invalid disk UUID: %s\n", argv[i + 1]);
				return EXIT_FAILURE;
			}
		}
	}

	int err = pure64_util_mkgpt(util, &disk_uuid);
	if (err != 0) {
		fprintf(stderr, "Failed to make partition table: %s\n", pure64_strerror(err));
		return EXIT_FAILURE;
	}

	return EXIT_SUCCESS;
}

