static int pure64_set_bootsector(struct pure64_util *util, int argc, const char **argv) {

	const void *bootsector_data = NULL;

	uint64_t bootsector_size = 0;

	for (int i = 0; i < argc; i++) {
		if (pure64_strcmp(argv[i], "mbr") == 0) {
			bootsector_data = mbr_data;
			bootsector_size = mbr_data_size;
		} else if (pure64_strcmp(argv[i], "pxe") == 0) {
			bootsector_data = pxe_data;
			bootsector_size = pxe_data_size;
		} else if (pure64_strcmp(argv[i], "multiboot") == 0) {
			bootsector_data = multiboot_data;
			bootsector_size = multiboot_data_size;
		} else if (pure64_strcmp(argv[i], "multiboot2") == 0) {
			bootsector_data = multiboot2_data;
			bootsector_size = multiboot2_data_size;
		} else if (is_opt(argv[i])) {
			fprintf(stderr, "Unknown option: %s\n", argv[i]);
			return EXIT_FAILURE;
		} else {
			fprintf(stderr, "Unknown bootsector: %s\n", argv[i]);
			return EXIT_FAILURE;
		}
	}

	int err = pure64_util_set_bootsector(util, bootsector_data, bootsector_size);
	if (err != 0) {
		fprintf(stderr, "Failed to set bootsector: %s\n", pure64_strerror(err));
		return EXIT_FAILURE;
	}

	return EXIT_SUCCESS;
}

static int pure64_set_stage_two(struct pure64_util *util, int argc, const char **argv) {

	(void) argc;
	(void) argv;

	int err = pure64_util_set_stage_two(util, pure64_data, pure64_data_size);
	if (err != 0) {
		fprintf(stderr, "Failed to set stage two: %s\n", pure64_strerror(err));
		return EXIT_FAILURE;
	}

	return EXIT_SUCCESS;
}

